	public void enviarMailConfirmacionCuenta(String destinatario, String codigoConfirmacion) {
		final String to = destinatario;
		final String from = RuffoConfig.getConfiguration().getString(RuffoConfig.CONFIRMAR_CUENTA_FROM);
		final String username = RuffoConfig.getConfiguration().getString(RuffoConfig.CONFIRMAR_CUENTA_USERNAME);
		final String password = RuffoConfig.getConfiguration().getString(RuffoConfig.CONFIRMAR_CUENTA_PASS);
		final String nombreImagen = RuffoConfig.getConfiguration().getString(RuffoConfig.CONFIRMAR_CUENTA_IMAGEN);

		// Assuming you are sending email through gmail
		String host = "smtp.gmail.com";
		Properties props = new Properties();
		props.put("mail.smtp.auth", "true");
		props.put("mail.smtp.starttls.enable", "true");
		props.put("mail.smtp.ssl.trust", "smtp.gmail.com");
		props.put("mail.smtp.host", host);
		props.put("mail.smtp.port", "25");

		Session session = Session.getInstance(props, new javax.mail.Authenticator() {
			protected PasswordAuthentication getPasswordAuthentication() {
				return new PasswordAuthentication(username, password);
			}
		});

		try {
			// Create a default MimeMessage object.
			Message message = new MimeMessage(session);
			// Set From: header field of the header.
			message.setFrom(new InternetAddress(from));
			// Set To: header field of the header.
			message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(to));
			// Set Subject: header field
			message.setSubject("Confirmacion de cuenta");
			// This mail has 2 part, the BODY and the embedded image
			MimeMultipart multipart = new MimeMultipart("related");
			// first part (the html)
			BodyPart messageBodyPart = new MimeBodyPart();
			String insertarImagen = StringUtils.isBlank(nombreImagen) ? "" : "<img src='cid:imagen' alt='Ruffo'>";
			String htmlText = "<H1>" +insertarImagen + "</H1>" + "<H3>Ingresa a la aplicacion para activar tu cuenta en RUFFO con el siguiente codigo: </H3>" + "<H2>" + codigoConfirmacion + "</H1>";
			messageBodyPart.setContent(htmlText, "text/html");
			// add it
			multipart.addBodyPart(messageBodyPart);

			if (StringUtils.isNotBlank(nombreImagen)) {
				// second part (the image)
				messageBodyPart = new MimeBodyPart();
				final String directorioImagenesAplicacion = obtenerDirectorioDeImagenesSegunSistemaOperativo();
				File carpeta = new File(directorioImagenesAplicacion);
				if (!carpeta.exists())
					carpeta.mkdirs();
				
				String rutaimagen = directorioImagenesAplicacion + System.getProperty("file.separator") + nombreImagen;
				
				try {
					File f = new File(rutaimagen);
					DataSource fds = new FileDataSource(rutaimagen);					
					if (fds != null && f.exists()) {
						messageBodyPart.setDataHandler(new DataHandler(fds));
						messageBodyPart.setHeader("Content-ID", "<imagen>");
						// add image to the multipart
						multipart.addBodyPart(messageBodyPart);					
					}
					else 
						log4j.fatal("El directorio "+ directorioImagenesAplicacion + "no existe y no se puede crear o la imagen no existe.");
				} catch (Exception e) {
					log4j.fatal(e.getMessage());
				}				
			}
			
			// put everything together
			message.setContent(multipart);
			// Send message
			Transport.send(message);
			log4j.info("Mail de confirmacion de cuenta enviado a la dirección: "+ to);
		} catch (MessagingException e) {
			log4j.fatal(e.getMessage());
			throw new RuntimeException(e);
		}
	}
	
