	private String obtenerDirectorioDeImagenesSegunSistemaOperativo() {
		String ruta = null;
		if (FuncionesUtilitarias.isMac())
			ruta= RuffoConfig.getConfiguration().getString(RuffoConfig.DIRECTORIO_IMAGENES_APLICACION_MAC);
		if (FuncionesUtilitarias.isWindows())
			ruta = RuffoConfig.getConfiguration().getString(RuffoConfig.DIRECTORIO_IMAGENES_APLICACION_WINDOWS);
		if (FuncionesUtilitarias.isUnix())
			ruta= RuffoConfig.getConfiguration().getString(RuffoConfig.DIRECTORIO_IMAGENES_APLICACION_UNIX);
		return ruta;
	}

